    drop table shows cascade constraints
    drop table theatres cascade constraints
    drop sequence app_users_seq
    drop sequence bookings_seq
    drop sequence cities_seq
	drop sequence movies_seq
    drop sequence shows_seq
    drop sequence theatres_seq
    create sequence app_users_seq start with 1 increment by 50
    create sequence bookings_seq start with 1 increment by 50
    create sequence cities_seq start with 1 increment by 50
    create sequence movies_seq start with 1 increment by 50
    create sequence shows_seq start with 1 increment by 50
    create sequence theatres_seq start with 1 increment by 50
    create table app_users (
        address_city number(10,0),
        age number(10,0),
        id number(10,0) not null,
        mobile number(19,0),
        email varchar2(255 char),
        name varchar2(255 char),
        password varchar2(255 char),
        primary key (id)
    )
    create table bookings (
        booked_by number(10,0),
        id number(10,0) not null,
        seats_booked number(10,0),
        show_id number(10,0),
        total_amount number(10,0),
        payment_status varchar2(255 char),
        status varchar2(255 char),
        primary key (id)
    )
    create table cities (
        id number(10,0) not null,
        name varchar2(255 char),
        primary key (id)
    )
    create table movies (
        id number(10,0) not null,
        duration number(19,0),
        description varchar2(255 char),
        genre varchar2(255 char),
        name varchar2(255 char),
		poster BLOB,
        primary key (id)
    )
    create table shows (
        available_seats number(10,0),
        id number(10,0) not null,
        movie_id number(10,0),
        price_per_person number(10,0),
        theatre_id number(10,0),
        start_time timestamp(6),
        primary key (id)
    )
    create table theatres (
        city_id number(10,0),
        id number(10,0) not null,
        name varchar2(255 char),
        primary key (id)
    )
